    function post_jquery(url, datos, success) {
        $.LoadingOverlay('show');
        $.post(url, datos, function (retorno) {
            if (retorno.success) {
                alerta_accion(retorno.mensaje, function () {
                    success();
                });
            } else {
                alerta(retorno.mensaje);
            }
        }, 'json').fail(function (retorno) {
            console.log(retorno);
            alerta_errores(retorno.responseText);
            alerta('Ha ocurrido un problema al enviar la información');
        }).always(function () {
            $.LoadingOverlay('hide');
        });
    }

    function get_jquery(url, datos, funcion, div = false) {
        div == false ? $.LoadingOverlay('show') : $('#' + div).LoadingOverlay('show');
        $.get(url, datos, function (retorno) {
            funcion(retorno);
        }).always(function () {
            div == false ? $.LoadingOverlay('hide') : $('#' + div).LoadingOverlay('hide');
        });
    }

    function post_ajax_admin(formulario, success, m, callback) {
        $.LoadingOverlay('show');
        var formData = new FormData(formulario[0]);
        //hacemos la petición ajax
        $.ajax({
            url: formulario.attr('action'),
            type: 'POST',
            data: formData,
            dataType: 'json',
            //necesario para subir archivos via ajax
            cache: false,
            contentType: false,
            processData: false,

            success: function (retorno) {
                if (!m) {
                    if (retorno.success) {
                        alerta_accion(retorno.mensaje, function () {
                            success();
                        });
                    } else {
                        alerta(retorno.mensaje);
                    }
                } else {
                    $.LoadingOverlay('hide');
                    msg += '' + retorno.mensaje;
                    callback(msg);
                }
                $.LoadingOverlay('hide');
            },
            //si ha ocurrido un error
            error: function (retorno) {
                console.log(retorno);
                alerta_errores(retorno.responseText);
                alerta('Hubo un problema en la envío de la información');
                $.LoadingOverlay('hide');
            }
        });
    }

    function cargar_url(url) {
        $.LoadingOverlay('show');
        location.href = '{{url('')}}/' + url;
        $.LoadingOverlay('hide');
